SET NAMES UTF8;   #设置客户端连接服务器编码utf8
DROP DATABASE IF EXISTS pet;   #如果存在pet数据库就删除
CREATE DATABASE pet CHARSET=UTF8;  #创建数据库并设置编码为UTF8
USE pet;    #使用pet数据库

/**************************************************************用户信息表*******************************************************************/
CREATE TABLE pet_user(
    uid INT PRIMARY KEY AUTO_INCREMENT,
    uname VARCHAR(32),
    upwd VARCHAR(32),
    email VARCHAR(64),
    phone VARCHAR(16),
    avatar VARCHAR(128),        #头像图片路径
    user_name VARCHAR(32),      #用户名
    gender INT                  #性别  0-女  1-男
);


/**用户信息**/
/*****************************************************************首页轮播广告商品*************************************************************/
CREATE TABLE pet_index_carlusel(
    cid INT PRIMARY KEY AUTO_INCREMENT, #商品cid
    img VARCHAR(128),                   #图片
    title VARCHAR(64),                  #商品描述
    href VARCHAR(128)                   #商品链接
);




/*****************************************************************宠物物品按作用分类***************************************************************/
CREATE TABLE pet_laptop_family(
  fid INT PRIMARY KEY AUTO_INCREMENT,
  fname VARCHAR(32)
);



/********************************************************************宠物用品*********************************************************************/
CREATE TABLE pet_laptop(
    lid INT PRIMARY KEY AUTO_INCREMENT,
    family_id INT,              #所属型号家族编号
    title VARCHAR(128),         #主标题
    price DECIMAL(10,2),        #价格
    promise VARCHAR(64),        #服务承诺
    spec VARCHAR(64),           #规格/颜色
    lname VARCHAR(32),          #商品名称
    texture VARCHAR(32),        #材质
    details VARCHAR(1024),      #产品详细说明
    shelf_time BIGINT,          #上架时间
    sold_count INT,             #已售出的数量
    is_onsale BOOLEAN           #是否促销中
);
/****************************************************************插入宠物用品***********************************************************************/


/*****************************************************************宠物用品图片***************************************************************************/
CREATE TABLE pet_laptop_pic(
  pid INT PRIMARY KEY AUTO_INCREMENT,
  laptop_id INT,              #宠物商品编号
  sm VARCHAR(128),            #小图片路径
  md VARCHAR(128),            #中图片路径
  lg VARCHAR(128)             #大图片路径
);

/********************************************************购物车条目***************************************************************************/
CREATE TABLE pet_shoppingcart_item(
  iid INT PRIMARY KEY AUTO_INCREMENT,
  user_id INT,      #用户编号
  product_id INT,   #商品编号
  count INT,        #购买数量
  is_checked BOOLEAN #是否已勾选，确定购买
);















